import collections
class Solution:
    def longestPalindrome(self, s: str) -> int:
        counter = collections.Counter()
        for c in s:
            counter[c] += 1
        ans = 0
        odd = 0
        for count in counter:
            if counter[count] % 2 == 0:
                ans += counter[count]
            else:
                ans += counter[count]-1
                odd = 1
        return odd + ans
