class Solution:
    def longestPalindrome(self, s: str) -> str:
        ans = ""
        for i in range(0, len(s)):
            if len(self.grow(s,i,i)) > len(ans):
                ans = self.grow(s,i,i)
            if len(self.grow(s,i,i + 1)) > len(ans):
                ans = self.grow(s,i,i + 1)
        return ans
    def grow(self, s, i, r):
        p = s[i]
        while i > -1 and r < len(s):
            if s[i] == s[r]:
                p = s[i : r + 1]
            else:
                return p
            i -= 1;r += 1
        return p


sol = Solution()
s = "babad"
